import{_ as i,I as l,c as a,o as n,j as o,al as s,J as p,a as r,w as d}from"./chunks/framework.CQoeh1S1.js";const k=JSON.parse('{"title":"모노레포(Monorepo) 정리","description":"프론트엔드 관점에서 모노레포와 멀티레포를 비교하고, 직접 구성해보며 느낀 점을 정리한다.","frontmatter":{"title":"모노레포(Monorepo) 정리","description":"프론트엔드 관점에서 모노레포와 멀티레포를 비교하고, 직접 구성해보며 느낀 점을 정리한다.","date":"2026-01-27T00:00:00.000Z","tags":["monorepo"],"head":[["link",{"rel":"canonical","href":"https://mslee98.github.io/sites/article/monorepo/"}]]},"headers":[],"relativePath":"sites/article/monorepo/index.md","filePath":"sites/article/monorepo/index.md"}'),c={name:"sites/article/monorepo/index.md"},b={id:"작성-중-2026-01-27",tabindex:"-1"};function m(h,e,g,_,u,f){const t=l("Badge");return n(),a("div",null,[o("h1",b,[p(t,{type:"warning"},{default:d(()=>[...e[0]||(e[0]=[r("작성 중 | 2026-01-27",-1)])]),_:1}),e[1]||(e[1]=r()),e[2]||(e[2]=o("a",{class:"header-anchor",href:"#작성-중-2026-01-27","aria-label":"Permalink to “작성 중 | 2026-01-27”"},"​",-1))]),e[3]||(e[3]=s('<h2 id="서론" tabindex="-1">서론 <a class="header-anchor" href="#서론" aria-label="Permalink to “서론”">​</a></h2><p>요즘 채용 공고를 보다 보면<br><strong><code>monorepo</code></strong>, <strong><code>디자인 시스템</code></strong>, <strong><code>공통 패키지</code></strong>, <strong><code>플랫폼 팀</code></strong> 같은 키워드가<br> 너무 자연스럽게 등장한다.</p><p>어느 순간부터 프론트엔드 생태계에서는<br> 이런 개념들이<br> “있으면 좋은 것”이 아니라<br><strong>웬만하면 갖추고 있어야 하는 기본값</strong>처럼 느껴진다.</p><p>규모가 있는 조직이라면<br> 플랫폼 팀이 공통 인프라와 개발 환경을 정리해주고,<br> 각 서비스 팀은 그 위에서 비교적 편하게 개발할 수 있다.</p><p>하지만 현실은 늘 그렇게 이상적이진 않다.</p><ul><li>플랫폼 팀은 없고</li><li>서비스는 하나둘 늘어나고</li><li>공통 코드는 생기기 시작하는데</li><li>구조를 잡아줄 사람은 딱히 없는 상황</li></ul><p>이런 환경에서는 결국<br><strong>구조를 고민하고, 선택하고, 책임지는 역할이 개발자 개인에게 돌아오는 경우</strong>가 많다.</p><hr><p>사실 개인적으로는<br> 플랫폼 팀이 하는 일들이 늘 궁금했다.</p><ul><li>공통 컴포넌트를 어떻게 설계하는지</li><li>디자인 시스템은 어떤 기준으로 나누는지</li><li>Storybook은 어디까지 쓰는 게 적절한지</li><li>CI/CD나 배포 파이프라인은 어떻게 구성하는지</li></ul><p>한 번쯤은<br><strong>“서비스 만드는 개발자”가 아니라<br> “개발 환경을 만드는 개발자”의 관점에서 일해보고 싶다는 생각</strong>도 계속 들었다.</p><p>이 시리즈는<br> “모노레포가 유행이니까 써보자”라는 이야기보다는,</p><ul><li>왜 이런 구조가 등장하게 되었는지</li><li>어떤 문제를 해결하려는 선택인지</li><li>그리고 이게 우리 팀, 혹은 나 혼자 하는 프로젝트에도 의미가 있는지</li></ul><p>를 <strong>프론트엔드 개발자 관점에서 천천히 정리해보는 기록</strong>이다.</p><p>정답을 알려주려는 글은 아니다.<br> 다만 이 글을 읽고 나서,</p><blockquote><p><em>“이 정도 규모라면 이런 선택도 가능하겠구나”</em><br><em>“이건 지금 우리 상황에서는 조금 과한 것 같네”</em></p></blockquote><p>같은 판단을<br> 스스로 내려볼 수 있다면,<br> 그걸로 충분하다고 생각한다.</p><h2 id="목차" tabindex="-1">목차 <a class="header-anchor" href="#목차" aria-label="Permalink to “목차”">​</a></h2><ul><li><p><a href="./introduction.html">모노레포란 무엇인가?</a><br> → 개념과 등장 배경, 왜 거론되기 시작했는지</p></li><li><p><a href="./pros-and-cons.html">Monorepo vs Multirepo 장단점</a><br> → 프론트엔드 관점에서의 현실적인 비교</p></li><li><p><a href="./tools.html">모노레포 초기 세팅</a><br> → 실제로 구성해보며 부딪힌 포인트들</p></li></ul>',19))])}const T=i(c,[["render",m]]);export{k as __pageData,T as default};
