import{_ as o,c as r,o as a,al as t}from"./chunks/framework.BiQzkkdA.js";const m=JSON.parse('{"title":"모노레포(Monorepo) 정리","description":"프론트엔드 관점에서 모노레포와 멀티레포를 비교하고, 직접 구성해보며 느낀 점을 정리한다.","frontmatter":{"title":"모노레포(Monorepo) 정리","description":"프론트엔드 관점에서 모노레포와 멀티레포를 비교하고, 직접 구성해보며 느낀 점을 정리한다.","date":"2026-01-27T00:00:00.000Z","tags":["monorepo"],"head":[["link",{"rel":"canonical","href":"https://mslee98.github.io/sites/article/monorepo/"}]]},"headers":[],"relativePath":"sites/article/monorepo/index.md","filePath":"sites/article/monorepo/index.md"}'),n={name:"sites/article/monorepo/index.md"};function i(s,e,l,p,c,d){return a(),r("div",null,[...e[0]||(e[0]=[t('<h1 id="" tabindex="-1"><a class="header-anchor" href="#" aria-label="Permalink to “”">​</a></h1><blockquote><p>작성일: 2026-01-27</p></blockquote><h2 id="서론" tabindex="-1">서론 <a class="header-anchor" href="#서론" aria-label="Permalink to “서론”">​</a></h2><p>요즘 이직을 준비하면서 느낀 점은<br> 프론트엔드 생태계가 생각보다 훨씬 복잡해졌다는 것이다.</p><p>Monorepo, 디자인 시스템, 공통 패키지, 플랫폼 팀 같은 개념들은<br> 이제 선택이 아니라 <strong>개발 생산성을 위한 기본 전제</strong>처럼 느껴진다.</p><p>규모가 있는 조직이라면 플랫폼 팀이 공통 인프라와 개발 환경을 관리하지만,<br> 그렇지 않은 환경에서는 이러한 구조를 <strong>개발자 개인이 이해하고 감당해야 하는 경우도 많다.</strong></p><p>이 글에서는<br> 프론트엔드 관점에서 <strong>모노레포가 왜 등장했고</strong>,<br><strong>어떤 문제를 해결하려는 전략인지</strong>를 차근히 정리해보려 한다.</p><h2 id="목차" tabindex="-1">목차 <a class="header-anchor" href="#목차" aria-label="Permalink to “목차”">​</a></h2><ul><li><a href="./introduction.html">모노레포란 무엇인가?</a></li><li><a href="./pros-and-cons.html">Monorepo vs Multirepo 장단점</a></li><li><a href="./tools.html">모노레포 초기 세팅</a></li></ul>',9)])])}const _=o(n,[["render",i]]);export{m as __pageData,_ as default};
